import React, { Component } from "react";
import ReactDOM from "react-dom";
import 'semantic-ui-css/semantic.min.css'
import "./styles.css";
import { NavBar } from "./navBar";
import { Input, Container } from "semantic-ui-react";
import Add from "./components/Add";
import View from "./components/View";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

class App extends Component {
  initialState = {
    users: [
      {
        name: "IDLY - 40 rs",
        username: "Idli, sambar and chutney are a South Indian’s idea of holy trinity. Contrary to popular belief, India’s favourite breakfast item is in fact not Indian at all. The original recipe belongs to present day Indonesia and the preparation is similar to what they call kedli. ",
        image:
          "https://thumbs.dreamstime.com/b/idly-tomato-chutney-traditional-ethnic-south-indian-breakfast-idli-rice-cake-served-coconut-sambar-32460705.jpg"
      },
      {
        name: "DOSA- 50rs",
        username: "Idli, sambar and chutney are a South Indian’s idea of holy trinity. Contrary to popular belief, India’s favourite breakfast item is in fact not Indian at all. The original recipe belongs to present day Indonesia and the preparation is similar to what they call kedli. ",
        image:
          "data:image/jpeg;base64,"
      },
      { name: "PIZZA- 200rs", username: "Pizza is one of the universally loved dishes today and known to one and all. From Chicago’s deep dish to thin crust, there are multiple options available. But the question arises, where does one find the best pizza? Well, if you are a true pizza lover, and ready to travel lengths for your food love,",
    image:"data:image/jpeg;base64," }
    ],
    results: [],
    query: ""
  };

  state = this.initialState;

  componentDidUpdate(prevProps, prevState) {
    if (prevState.users !== this.state.users) {
      this.resetSearch();
    }
  }

  search = (event) => {
    const { users } = this.state;
    const { value } = event.target;

    this.setState({ query: value });

    const results = users.filter((user) => {
      const regex = new RegExp(value, "gi");
      return user.name.match(regex);
    });

    this.setState({ results });
  };

  resetSearch = () => {
    const { users, query } = this.state;

    const results = users.filter((user) => {
      const regex = new RegExp(query, "gi");
      return user.name.match(regex);
    });

    this.setState({ results });
  };

  getUserById = (id) => {
    const { users } = this.state;

    const u = users.filter((user) => user.name === id);

    return u[0];
  };

  addRow = (user) => {
    const { users } = this.state;
    console.log(users);

    this.setState({ users: [...users, user] });
  };

  updateRow = (id, updatedUser) => {
    const { users } = this.state;

    this.setState({
      users: users.map((user) => (user.name === id ? updatedUser : user))
    });
  };

  deleteRow = (id) => {
    const { users } = this.state;

    this.setState({
      users: users.filter((user) => user.name !== id)
    });
  };

  render() {
    const { users, results, query } = this.state;
    const data = results.length === 0 && !query ? users : results;

    return (
      <>
        <Router>
          <NavBar />

          <Switch>
            <Route exact path="/"></Route>

            <Route exact path="/listusers">
              <Container>
                <Input
                  icon="search"
                  onChange={this.search}
                  placeholder="Search"
                  style={{ padding: "10px", marginLeft: "500px" }}
                />
                <View
                  data={data}
                  deleteRow={this.deleteRow}
                  updateRow={this.updateRow}
                  getUserById={this.getUserById}
                />
              </Container>
            </Route>

            <Route exact path="/adduser">
              <Add addRow={this.addRow} />
            </Route>
          </Switch>
        </Router>
      </>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
